import React, { useState } from "react";

function App() {
  const [source, setSource] = useState("");
  const [curry, setCurry] = useState("");
  const [byteCode, setByteCode] = useState("");

  const handleCompile = () => {
    // Example logic for compilation
    setByteCode(`ByteCode generated from:\nSource: ${source}\nCurry: ${curry}`);
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      {/* Top Section */}
      <div style={{ display: "flex", marginBottom: "20px", gap: "20px" }}>
        {/* Source Section */}
        <div style={{ flex: 2, padding:10 }}>
          <label
            htmlFor="source"
            style={{ display: "block", marginBottom: "10px", fontWeight: "bold" }}
          >
            Source
          </label>
          <textarea
            id="source"
            value={source}
            onChange={(e) => setSource(e.target.value)}
            placeholder="Enter source code..."
            rows="20"
            style={{
              width: "100%",
              padding: "10px",
              fontSize: "16px",
              border: "1px solid #ccc",
              borderRadius: "5px",
              resize: "none",
            }}
          />
        </div>
        {/* Curry Section */}
        <div style={{ flex: 1,padding:10  }}>
          <label
            htmlFor="curry"
            style={{ display: "block", marginBottom: "10px", fontWeight: "bold" }}
          >
            Curry
          </label>
          <textarea
            id="curry"
            value={curry}
            onChange={(e) => setCurry(e.target.value)}
            placeholder="Enter curry code..."
            rows="20"
            style={{
              width: "100%",
              padding: "10px",
              fontSize: "16px",
              border: "1px solid #ccc",
              borderRadius: "5px",
              resize: "none",
            }}
          />
        </div>
      </div>
      {/* Compile Button */}
      <div style={{ textAlign: "center", marginBottom: "20px" }}>
        <button
          onClick={handleCompile}
          style={{
            padding: "10px 20px",
            fontSize: "16px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Compile
        </button>
      </div>
      {/* Byte Code Section */}
      <div style={{ textAlign: "center" }}>
        <label
          htmlFor="byteCode"
          style={{ display: "block", marginBottom: "10px", fontWeight: "bold" }}
        >
          Byte Code
        </label>
        <textarea
          id="byteCode"
          value={byteCode}
          readOnly
          rows="2"
          style={{
            width: "100%",
            padding: "10px",
            fontSize: "16px",
            border: "1px solid #ccc",
            borderRadius: "5px",
            backgroundColor: "#f8f9fa",
            color: "#333",
            resize: "none",
          }}
        />
      </div>
    </div>
  );
}

export default App;
